INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (6, '178447', 'Kali linux 渗透攻击之社会工程学攻击', 'www.freebuf.com/column/178447.html', 'http://image.3001.net/images/20180723/153232687582712.png!240.160', '社会工程学是利用人性的弱点体察、获取有价值信息的实践方法，它是一种期盼的艺术。在缺少目标系统的必要信息时，社会工程学技术是渗透测试人员获取信息的至关重要的手段。对于素有类型的组织而言，人都是安全防范措施李最为薄弱的一个环节，也是整个安全基础设施最脆弱的层面。', 'dairan', 'http://image.3001.net/images/index/wp-user-avatar-50x50.png', 'www.freebuf.com/author/dairan', '25天前', '0', '4038', 0);
