INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (72, '155983', '一次开源bug问题管理平台建设探索', 'www.freebuf.com/column/155983.html', 'http://image.3001.net/images/20171204/15123558412698.png!240.160', '我经常逛freebuf，在看到一些帖子的时候，也会把深埋在脑海中的需求挖出来，看能不能借此实现。最近，我想圆了我bug统一管理的梦。', 'q11132o0', 'http://image.3001.net/images/20171106/15099354779767.jpg!video', 'www.freebuf.com/author/q11132o0', '256天前', '6', '670271', 0);
