INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (74, '180714', '企业安全项目 | 前端绕过专项整改', 'www.freebuf.com/column/180714.html', 'http://image.3001.net/images/20180812/1534084714_5b70466abf069.png!240.160', '这里所指的前端安全，听上去像是关于JS、HTML5等方面的安全，但其实不然，实则是一些关于会员体系方面业务逻辑漏洞的整改。前端绕过专项整改是我加入公司后，参与的第一个“伤筋动骨”（涉及底层逻辑设计，影响整个公司业务）类安全项目，也差不多是公司内部自己开展的第一个影响范围广的业务相关安全项目。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '5天前', '0', '1249', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (75, '179047', '企业安全项目 | 短信验证码安全', 'www.freebuf.com/column/179047.html', 'http://image.3001.net/images/20180731/1533006689_5b5fd36190bd2.jpg!240.160', '谈起短信验证码的安全，首先从脑海中蹦出来的可能有：短信炸弹、验证码暴力破解、验证码重复利用、短信验证绕过……追究其根源，大致可以分为短信相关接口、验证码的特性甚至与业务逻辑验证相关联。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '19天前', '4', '95755', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (76, '178342', '企业安全项目 | Github信息泄露专项', 'www.freebuf.com/column/178342.html', 'http://image.3001.net/images/20180723/15323122446499.jpg!240.160', '企业的信息泄露途径很多，常见的以github信息泄露为例。既需要有规章制度来约束涉及到代码的员工，又需要有技术手段进行监测，针对违规情况提供技术手段进行处罚 ，实现管、技、制相结合。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '26天前', '2', '168465', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (77, '177573', '企业安全项目|测试环境内网化', 'www.freebuf.com/column/177573.html', 'http://image.3001.net/images/20180716/15317239757732.jpg!240.160', '经过长时间的风险发掘与分析，企业面临的安全风险已逐渐清晰并有了比较全面的视野。从投入与产出比的角度出发，结合当前正在承受的危害和实施难易程度考虑，可以将前期所规划的安全项目排入实施计划周期并进行推动。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '34天前', '2', '206012', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (78, '176887', '企业安全架构建设', 'www.freebuf.com/column/176887.html', 'http://image.3001.net/images/20180707/153096350746853.png!240.160', '在【企业安全】系列文章中，最怕出错的其实就是本文。因为涉及到安全组织架构、安全架构规划等具有相当高度的内容，很可能会因为视野狭窄与经验不够丰富等因素给大家带来误解。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '41天前', '0', '84571', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (79, '176243', '企业安全威胁简述', 'www.freebuf.com/column/176243.html', 'http://image.3001.net/images/20180701/15304379851061.png!240.160', '企业面临的威胁可以说是来自四面八方，既有恶意的外部攻击者，也不排除来自内部的安全隐患。往往在不经意间，又可能引入新的安全因素，也有可能在输出产品时给其他企业带来安全风险。大而言之，企业的威胁从个人理解的角度出发，大致可以分为以下几点：\n\n输入威胁、内部威胁、外部威胁、输出威胁', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '47天前', '0', '81256', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (80, '180714', '企业安全项目 | 前端绕过专项整改', 'www.freebuf.com/column/180714.html', 'http://image.3001.net/images/20180812/1534084714_5b70466abf069.png!240.160', '这里所指的前端安全，听上去像是关于JS、HTML5等方面的安全，但其实不然，实则是一些关于会员体系方面业务逻辑漏洞的整改。前端绕过专项整改是我加入公司后，参与的第一个“伤筋动骨”（涉及底层逻辑设计，影响整个公司业务）类安全项目，也差不多是公司内部自己开展的第一个影响范围广的业务相关安全项目。', '阿尔法之梦', 'http://image.3001.net/2014/07/13.png', 'www.freebuf.com/author/阿尔法之梦', '5天前', '0', '1249', 0);
