INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (203, '160227', '随笔：云计算背后的运维，你了解多少？', 'www.freebuf.com/column/160227.html', 'http://image.3001.net/images/20180115/15159840467809.png!240.160', '即使不是IT人，很多都经历过这样的情形：使用X家的操作系统，B家的应用程序，在出现问题的时候两家互相推诿的现象。那么云计算的背后，有多少服务商参与呢？出现问题该怎么办？', 'craneliu', 'http://image.3001.net/images/20170421/14927628174063.jpg!video', 'www.freebuf.com/author/craneliu', '214天前', '1', '24199', 0);
