INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (206, '158039', '随笔：“知所应知”，你所不知道的秘密', 'www.freebuf.com/column/158039.html', 'http://image.3001.net/images/20171222/15139426456579.png!240.160', '作为IT安全人员，相信每一位都知道“知所必须”原则，结合“最小权限”的就是两个“最小化”原则，是很多IT安全人员在入门课上就学到的。怎样才是最小，相信也是IT安全人员一直孜孜追求的，很多人困惑于“如何将信息尽可能少的传递、权限尽可能小的授予，并能保证该岗位执行其正常工作。', 'craneliu', 'http://image.3001.net/images/20170421/14927628174063.jpg!video', 'www.freebuf.com/author/craneliu', '238天前', '0', '23897', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (207, '157502', '随笔：DevOps关键成功因素', 'www.freebuf.com/column/157502.html', 'http://image.3001.net/images/20171218/15135888637288.png!240.160', '在企业中，软件开发和软件运维通常是由不同部门（组）来完成的。现实环境中，随着业务竞争对软件交付速度的提升，DevOps 逐渐成熟并为更多的软件人员所采用。', 'craneliu', 'http://image.3001.net/images/20170421/14927628174063.jpg!video', 'www.freebuf.com/author/craneliu', '242天前', '0', '19091', 0);
