INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (248, '158250', 'AI安全如何入门（上）', 'www.freebuf.com/column/158250.html', 'http://image.3001.net/images/20171225/15142130945152.png!240.160', '读者经常问我如何入门AI安全，我简单写了一下我的理解，希望可以帮助到大家。AI安全目前属于发展早期，就像刚发明的汽车，不一定比马车强。选择造更快的马车还是造汽车，只有最适合自己的答案，没有最好的答案。', '兜哥', 'http://image.3001.net/2017/01/70ff24cf2ca563c59fd4e0c5db96aa13.jpeg', 'www.freebuf.com/author/兜哥', '235天前', '4', '1937199', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (249, '153810', '我的安全产品观（一）', 'www.freebuf.com/column/153810.html', 'http://image.3001.net/images/20171114/15106298138050.jpg!240.160', '同其他互联网产品一样，一个完整的安全产品团队至少会包括产品经理／技术／运营／销售这四个角色，安全产品从规划-设计-研发-上线-推广-运营，就一直是这四股力量相辅相承相生相克的过程。', '兜哥', 'http://image.3001.net/2017/01/70ff24cf2ca563c59fd4e0c5db96aa13.jpeg', 'www.freebuf.com/author/兜哥', '277天前', '1', '865854', 0);
