INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (274, '132768', '网络安全法的合规投入是一种投资', 'www.freebuf.com/column/132768.html', 'http://image.3001.net/2017/04/3feac2548a6cbb69ad30e0da9cb84ee0.jpeg!240.160', '如果网络安全法生效后，迅速产生像高通反垄断处罚那样的案例，那么今天那些扎实投入认真贯彻网络安全法合规的企业，其投入就是投资，免受处罚就是它们最好的回报。2016', '刘春泉', 'http://image.3001.net/images/20170420/14926846407305.jpg!video', 'www.freebuf.com/author/刘春泉', '484天前', '0', '17041', 0);
