INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (335, '157124', 'Fit 2018袁劲松专访：AI时代的智能引力与FreeBuf新品矩阵解读', 'www.freebuf.com/column/157124.html', 'http://image.3001.net/images/20171214/15132593266306.png!240.160', '今天的记者们很默契地分成了两派，技术派专攻AI智能引力方面的话题，社会派则对袁劲松演讲中提到的FreeBuf新品矩阵更感兴趣。究竟是哪一派的讨论更有意思？我们一起往下看。', 'Akane', 'http://image.3001.net/images/20170523/14955475494078.jpg!video', 'www.freebuf.com/author/Akane', '246天前', '0', '50408', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (336, '156206', '360 IoT 研究院院长李康：安全对人工智能的需求  ——从机器学习到机器创造', 'www.freebuf.com/column/156206.html', 'http://image.3001.net/images/20171208/15126999849178.png!240.160', '未来当人工智能达到能够自我构建机器并编程的阶段后，做安全攻防的研究人员需要考虑的是如何对抗具有自动机器构造能力的对手。攻防对抗是无止境的，只要我们人类还在。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '255天前', '0', '75591', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (337, '149437', '孝道科技3周岁专访创始人兼CEO范丙华：肩负使命、继续前行', 'www.freebuf.com/column/149437.html', 'http://image.3001.net/images/20170928/15065876085501.png!240.160', '三年来，犹如三年的酒，醇厚浓郁；也犹如孩童般的成长，不断探索、实践业务方向和发展目标，每走一步都伴随着艰辛的付出与痛苦的取舍。', 'Akane', 'http://image.3001.net/images/20170523/14955475494078.jpg!video', 'www.freebuf.com/author/Akane', '323天前', '0', '81055', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (338, '149043', '思科：用AI创造下一代网络架构 | 国家网络安全宣传周展商专访', 'www.freebuf.com/column/149043.html', 'http://image.3001.net/images/20170926/15063985411718.png!240.160', '趁着本届国家安全周的机会，我们有幸探访了思科，听听这个曾经创造了网络的巨头对网络安全的新思考。', 'Sphinx', 'http://image.3001.net/images/20170727/15011492194688.jpg!video', 'www.freebuf.com/author/Sphinx', '325天前', '0', '91635', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (339, '148480', '国家网络安全宣传周 FreeBuf 专访 | 绿盟科技：商业价值以外，更多的是社会责任', 'www.freebuf.com/column/148480.html', 'http://image.3001.net/2017/09/bf3d9094302f502f4d9318f2e6807230.jpeg!240.160', '采访计划原定只有一个小时，但和庞老师聊了足有90多分钟。没有太多套路，有的只是一位老安全人的侃侃而谈，都是肺腑之言。', 'Akane', 'http://image.3001.net/images/20170523/14955475494078.jpg!video', 'www.freebuf.com/author/Akane', '331天前', '0', '508628', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (340, '148189', '国家网络安全宣传周 FreeBuf 专访 | 杭州美创沈武林：把握风口，聚焦创新', 'www.freebuf.com/column/148189.html', 'http://image.3001.net/images/20170919/15058067067741.png!240.160', '9 月 17 日是国家网络安全宣传周展会正式开始的第一天，杭州美创营销总监、北京分公司总经理沈武林亲自上阵，为自家产品站台。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '332天前', '0', '92195', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (341, '147503', '专访周鸿祎：大安全时代，我们应站在更高的格局看问题', 'www.freebuf.com/column/147503.html', 'http://image.3001.net/images/20170912/15052180625884.png!240.160', '2017 ISC互联网安全大会，FreeBuf有幸受邀参加了“红衣教主”周鸿祎的会后专访。老周还是那么能侃，不过这次明显能感觉到他收着劲儿呢。', 'Akane', 'http://image.3001.net/images/20170523/14955475494078.jpg!video', 'www.freebuf.com/author/Akane', '339天前', '1', '133260', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (342, '146022', 'CSO人物 | 上海工业自动化仪表研究所王英：心之所向，步履不停', 'www.freebuf.com/column/146022.html', 'http://image.3001.net/images/20170830/1504073123637.png!240.160', '安全是国际的，不是一个国家、一个地区、一个工厂的问题。人类是一个大社会，我们在这个生态圈，我要谨慎，不会违背自己的良心，我要守住最后的底线，这是我的使命。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '352天前', '0', '90295', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (343, '145448', 'HackerOne CEO专访：我们正在打造全球最大的安全人才库', 'www.freebuf.com/column/145448.html', 'http://image.3001.net/images/20170824/1503546167126.png!240.160', '在这篇访谈中，美国国际数据集团CCO John Gallant与Marten Mickos就HackerOne的运营模式和平台机制作了深入交流，同时，还对主流公司如何接受安全众测的态度和观点作了探讨。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '358天前', '0', '67756', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (344, '144737', '专访：天才黑客Gabriel Bergel的黑客人生', 'www.freebuf.com/column/144737.html', 'http://image.3001.net/images/20170818/15030417575448.png!240.160', 'Gabriel可以算得上是信息安全界的摇滚明星了，除此之外，他还是一个崇尚维京文化（Viking）的半机械人。他十分热爱维京人的文化，并且他还在自己的双手中植入了两块电子芯片。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '364天前', '0', '68702', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (345, '143916', 'CTF的现实意义究竟在哪儿？FreeBuf专访清华大学网研院诸葛建伟博士', 'www.freebuf.com/column/143916.html', 'http://image.3001.net/images/20170810/15023620365072.png!240.160', '这可能是一段值得大书特书的历史，它所表达的或许还是中国在CTF国际赛事上的逐步崛起。蓝莲花战队创始人之一的诸葛建伟博士，将这样一群人形容为安全领域的“学院派”。在我们看来，如果这样一批安全科班出身的学院派都无法代表国内安全行业的理论最高水平，那么究竟怎样一批人才是呢？', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '372天前', '0', '58761', 0);
INSERT INTO `content`(`id`, `page_id`, `title`, `page_url`, `imgUrl`, `contents`, `author`, `authorImg`, `authorUrl`, `deploytime`, `comMount`, `readCount`, `money`) VALUES (346, '143021', '【读家】专访killer: 计算机病毒大多没有技术含量', 'www.freebuf.com/column/143021.html', 'http://image.3001.net/images/20170803/15017434035.png!240.160', '如果突然提起“killer”，想必大部分人会认为是在说杀手，不会想到这是一位反病毒专家的“花名”，但如果你知道“熊猫烧香”，你或许会对他有所耳闻。', 'AngelaY', 'http://image.3001.net/images/20170614/14974414804130.gif!video', 'www.freebuf.com/author/AngelaY', '379天前', '2', '57865', 0);
